        /**
         * Public method to close the flow's UI
         */
        app.closeFlow = function (successUrl) {
            _destroy();

            if (successUrl) {
                top.location.href = successUrl;
            }
        };

        /**
         * Public method to determine if the flow is active
         */
        app.isOpen = function () {
            return isOpen;
        };


        /**
         * Public method to restart the flow if Minibrowser focus is lost
         */
        app.restartFlow = function (url) {

            if (app.win) {
                app.win.focus();
            } else {
                url = url || config.landingUrl;
                app.startFlow(url);

                return false;
            }


        };

        /**
         * Public method for merchant to show error message
         */
        app.showError = function (msg) {
            _buildDOM({
                error: msg
            });
            _createMask();
            _bindEvents();

            isOpen = true;
        };

        // Return public methods
        return app;

