jQuery.fn.ticker = function(settings) {
	// adapted from danielvlopes jquery.ticker
	// https://github.com/danielvlopes/jquery.ticker
	
	var ticker = $(this);
	var itemsCollection =  ticker.find('li');
	var tickerContainerHeight = $(".tickerContainer").height();
	
	// config
	var current=0;
	var paused=0;
	var interval=4000;
	
	// look for interval attribute on ul to override default config
	if (ticker.attr('interval')) interval = ticker.attr('interval');
	
	// fix the height of the container to the tallest list item
	itemsCollection.each(function() {
		if ($(this).height() > ticker.height()) {
			ticker.height($(this).height());
		}
	});

	textHeight();	
	// show the first item
	//itemsCollection.hide().filter(":eq(0)").show().add("li");

	// monitor hover on and off
	itemsCollection.hover(
		function(){ paused=1; },
		function(){ paused=0; }
	);

	function tick() {
		// do nothing on hover
		if (paused){ return; };

		// hide current tick
		ticker.find("li:eq("+current+")").fadeOut("slow",function(){
		  $(this).hide();
		});

		// show next tick
		current = ++current%itemsCollection.size();
		ticker.find("li:eq("+current+")").fadeIn("slow");
		textHeight();
	}
	
	// calculate the padding-top so the text is centered within li
	function textHeight() {
		var textHeight = ticker.find("li:eq("+current+")").children().height();
		var offsetText = (tickerContainerHeight - textHeight) / 2;
		ticker.find("li:eq("+current+")").children().css("padding-top", offsetText)
	}
	
	// don't fade if there is only 1 item
	if (itemsCollection.length > 1) {
		setInterval(tick,interval);
	}
};
