var isFirstTime = false;


var currentTitle = '';

function ooyalacallback(playerId, eventName, eventArgs) {


    switch (eventName) {
        case "apiReady":
            if (!isFirstTime) {

                onReady();
                isFirstTime = true;


            }
            setTitle();
            break;
        case "stateChanged":
            var state = document.getElementById("player").getState();
            if (state == 'playing') {

                setTitle();
            }


            break;
        default:
            break;
    }
}


(function ($) {

    $.fn.lboxPlayer = function(options) {
        var me = $(this);
        me.each(function() {
            var settings = {
                embedCode: '',
                width: 956,
                height: 752,
                title: '',
                assetType: ''
            };
            settings = $.extend(settings, options || {});

            var embedCode = settings.embedCode;

            me.fancybox({
                'titleShow': true,
                titlePosition: 'inside',
                title: '&nbsp;',
                autoDimensions: false,
                autoScale: false,
                'transitionIn': 'elastic',
                'transitionOut': 'elastic',
                overlayOpacity: 0.6,
                overlayColor: '#303030',
                showNavArrows: false,
                padding: 0,
                margin: 1,
                width: settings.width,
                height: settings.height,
                onComplete: function () {
                    //append containter video list if asset is not video type

                    if (settings.assetType != 'video') {

                        drawVideoList(embedCode, settings.title);

                    }
                },
                onCleanup: function () {
                    $('#placeHolderPlayer').css({
                        top: -10000,
                        left: -10000
                    });

                    document.getElementById("player").pauseMovie();

                    if (typeof(firstEmbedCode) != 'undefined' && firstEmbedCode != '') {
                        //move first video
                        document.getElementById("player").changeCurrentItem(firstEmbedCode);
                    }
                    //reset play head time
                    document.getElementById("player").setPlayheadTime(0);
                },
                onFinish: function(wrap) {

                    if (wrap) {


                        $('#placeHolderPlayer').css({
                            'top'        : wrap.position().top + 'px',
                            'left'        : wrap.position().left + 'px'
                        });
                    }
                    //play movie
                    var player = document.getElementById("player");
                    if (player && player.setQueryStringParameters) {
                        player.setQueryStringParameters({embedCode: embedCode, autoplay:1});
                        player.playMovie();
                    }

                }
            });	//end fancybox
        }); //end each
        return me;
    }
})(jQuery);

function onReady() {

    $('.play').css("cursor", "pointer");

    $('.play').each(function() {
        var me = $(this);
        var title = me.attr('atitle');
        var type = me.attr('atype');
        var embedCode = me.attr('aid');

        height = 752;

        //if type asset is 'video' do not show video list
        if (type == 'video') {
            height -= 143;
        }

        var settings = {
            embedCode: embedCode,
            width: 956,
            height: height,
            title: title,
            assetType: type
        };

        me.lboxPlayer(settings);
    });
}

//bind html of video list
function buildHtmlVideoList(title) {

    var html = '<div class="prev">'
    html += '<div class="prev-image"></div>'
    html += '</div>'
    html += '<div class="center">'
    html += '<div class="channel-list-title">' + title + '</div>'
    html += '<div class="channel-list-container">'
    html += '<ul class="carousel"></ul>'
    html += '</div></div>'
    html += '<div class="next">'
    html += '<div class="next-image"></div>'
    html += '</div>'
    html += '<div class="clear"></div>';
    return html;
}

//define variable to mark embed code of first video
var firstEmbedCode = '';

//show video list
function drawVideoList(embedCode, title) {

    var html = buildHtmlVideoList(title);

    //remove channel list div if have before
    $('#fancybox-inner').parent().find('.channel-list').remove();

    $('#fancybox-inner').parent().append('<div class="channel-list">' + html + '</div>');

    var carousel = $('#fancybox-wrap .channel-list-container .carousel');

    getVideoList(embedCode, function(items) {
        if (items && typeof(items.error) != 'undefined') {
            carousel.html(items.message);
            return;
        }

        var n = items.length;

        var i = 0;

        var html = '';

        for (i = 0; i < n; i++) {
            /****
             *  ptitle: title of video
             *  aid: embed code
             * **/

            html += '<li class="item" ptitle="' + items[i].name + '" aid="' + items[i].embed_code + '">';

            if (i == 0) {
                firstEmbedCode = items[i].embed_code;
            }
            html += '<img src="' + items[i].preview_image_url + '" alt="" title="" />';

            html += '</li>';
        }

        carousel.html(html);

        //bind event play for each video
        $('.item', carousel).find('img').each(function() {
            var me = $(this);
            me.click(function() {
                var embedCode = me.parent().attr('aid');
                document.getElementById('player').setQueryStringParameters({embedCode: embedCode, autoplay:1});
            });
        });

        carousel.jcarousel({
            buttonNextHTML: null,
            buttonPrevHTML: null,
            scroll: 5,
            itemFallbackDimension: 152,
            initCallback: function(carousel) {
                carousel.buttonNext = $('.channel-list .next .next-image');
                carousel.buttonPrev = $('.channel-list .prev .prev-image');
            }
        });

    });
}

function getVideoList(embedCode, callback) {
    var url = "/application/getVideoList";
    $.ajax({
        type: "GET",
        url: url,
        dataType: 'json',
        data: {embed_code: embedCode},
        success: function(ret) {
            if ($.isFunction(callback)) {
                callback(ret);
            }
        },
        error: function() {
            if ($.isFunction(callback)) {
                var items = {error:true, message: 'Have a problem while connecting to server. Please try again.'}
                callback(items);
            }
        }
    });
}

function setTitle() {

    var currentTitle = document.getElementById("player").getCurrentItemTitle();

    if (currentTitle && currentTitle.length > 58) {
        currentTitle = currentTitle.substr(0, 58) + '...';
    }
    $('.fancybox-subtitle').html(currentTitle);
}






















