function setTrainerFilterCookies() {
    var selectedcourses = [];
    var country = '';
    var selectedcoursestr = "";
    var m = 0;
    $("#search_Trainers_chkboxesDiv").children("input:checked").map(function() {
        if (this.name == 'courses') {
            selectedcourses[m] = this.value;
            m++;
        }
    });
    if (selectedcourses.length == 0) {
        selectedcourses[0] = " ";
    }
    if ($(".pcs-Select option:selected").val() == '') {
        country = '';
    }
    else {
        country = $(".pcs-Select option:selected").val();
    }
    if (selectedcourses.length > 1) {
        for (var w = 0; w < selectedcourses.length; w++) {
            if (w == selectedcourses.length - 1) {
                selectedcoursestr = selectedcoursestr + selectedcourses[w];
            }
            else {
                selectedcoursestr = selectedcoursestr + selectedcourses[w] + "%2C";
            }
        }
    }
    else {
        selectedcoursestr = selectedcourses[0];
    }
    var exdate = new Date();
    exdate.setMinutes(exdate.getMinutes() + 5);

    document.cookie = "trainers_filter=" + "city_selected%3D" + sort_by_city + "#first_name%3D" + sort_by_first_name+ "#is_first_name_checked%3D" + is_first_name_checked+ "#last_name%3D" + sort_by_last_name+ "#is_last_name_checked%3D" + is_last_name_checked+ "#country_selected%3D" + country + "#courses_selected%3D" + selectedcoursestr +"#cityChecked%3D" + is_city_checked + "#state_selected%3D" + sort_by_state + "#stateChecked%3D" + is_state_checked + "#is_city_state_search_checked%3D" + is_city_state_search_checked + "#city_state_search_text%3D" + city_state_search_text + "#" + "#is_status_checked%3D" + is_status_checked + "#"+ "#status_selected%3D" + sort_by_status + "#"+";expires=" + exdate + ";path=/";


}


$(document).ready(function() {
    var i,x,y,ARRcookies = document.cookie.split(";");
    for (i = 0; i < ARRcookies.length; i++) {
        x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="));
        y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1);
        x = x.replace(/^\s+|\s+$/g, "");
        if (x == "trainers_filter") {
            sort_by_first_name = parseInt(y.split("first_name%3D")[1].split("#")[0]);
            sort_by_last_name = parseInt(y.split("last_name%3D")[1].split("#")[0]);
            sort_by_city = parseInt(y.split("city_selected%3D")[1].split("#")[0]);
            sort_by_state = parseInt(y.split("state_selected%3D")[1].split("#")[0]);
            sort_by_status = parseInt(y.split("status_selected%3D")[1].split("#")[0]);
            city_state_search_text= $.trim($("#city_state_search_txt").val());
            is_city_state_search_checked=parseInt(y.split("is_city_state_search_checked%3D")[1].split("#")[0]);
        }
    }

    document.cookie = 'trainers_filter=; expires=Thu, 01 Jan 1970 00:00:01 GMT;path=/';
});








