/*
Clear Text Plugin.  Removes value from text input boxes onFocus, replaces value onBlur.
*/

$.fn.search = function() {
    return this.focus(function() {
        if( this.value == this.defaultValue ) {
            this.value = "";
        }
    }).blur(function() {
        if( !this.value.length ) {
            this.value = this.defaultValue;
        }
    });
};

